class Solution {
public:
    bool buddyStrings(string s, string goal) {
        map<char, int> mp;
        int c = 0;
        vector<int> arr;
        for(int i = 0; i < s.length(); i++){
            if(goal[i] != s[i]){
                c++;
                arr.push_back(i);
            }
            mp[s[i]]++;
        }
        if(c == 0){
            for(auto i : mp){
                if(i.second >= 2){
                    return true;
                }
            }
            return false;
        }else if(c == 2){
            swap(s[arr[0]], s[arr[1]]);
            return s == goal; 
        }
        return false;
    }
};
