class Solution {
public:
    bool checkValidGrid(vector<vector<int>>& grid) {
        map<int, pair<int, int>> m;
        for(int i = 0; i < grid.size();i++){
            for(int j = 0; j < grid[0].size();j++){
                m[grid[i][j]] = make_pair(i, j);
            }
        }
        int n = grid.size(), o = grid[0].size();
        pair<int, int> prev = {0, 0};
        for(int i = 1; i < n * n;i++){
            pair<int, int> curr = m[i];
            int x = curr.first - prev.first;
            int y = curr.second - prev.second;
            prev = curr;
            if(x == -2 && y == 1)continue;
            else if(x == -1 && y == 2)continue;
            else if(x == 1 && y == 2)continue;
            else if(x == 2 && y == 1)continue;
            else if(x == 2 && y == -1)continue;
            else if(x == 1 && y == -2)continue;
            else if(x == -1 && y == -2)continue;
            else if(x == -2 && y == -1)continue;
            else {
                return false;
            }
        }
        return true;
    }
};
