class Solution {
public:

    map<string, bool> m;
    bool find(string s, int cnt) {
        if(s.empty()) {
            if(cnt >= 2) 
                return true;
            return false;
        }

        bool ok = false;
        for(int i  = 1; i <= s.size(); i++) {
            string t = s.substr(0, i);
            if(m[t]) {
                ok |= find(s.substr(i), cnt + 1);
            }
        }

        return ok;
    }
    vector<string> findAllConcatenatedWordsInADict(vector<string>& words) {
        for(auto i : words) {
            m[i] = true;
        }


        vector<string> ans;
        for(auto i : words) {
            if(find(i, 0)) {
                ans.push_back(i);
            }
        }

        return ans;

    }
};
