class Solution {
public:

    int solve(vector<int> &v, int s, int e){
        if(s == e) return s;
        int mid = (s +e)/2;
        if(v[mid] > v[mid+1]) return solve(v, s, mid);
        return solve(v, mid+1, e);

    }
    vector<int> findPeakGrid(vector<vector<int>>& mat) {
        pair<int, int> p;
        for(int i=0;i<mat.size();i++){
            vector<int> v = mat[i];
            int j = solve(v, 0, v.size()-1);

            if(i-1 >= 0 and i+1 < mat.size()){
                if(mat[i-1][j] < mat[i][j] and mat[i+1][j] < mat[i][j]){
                    p = {i, j};
                } 
            }
            else if(i-1 >= 0){
                if(mat[i-1][j] < mat[i][j]) {
                    p = {i, j};
                }
            }
            else {
                if(mat[i+1][j] < mat[i][j]){
                     p = {i, j};
                }
            }
        }

        return {p.first, p.second};
    }
};
